var SHEET_ID = "1D1YmEoR1JkncPDtHBEXBOc5ji5TDA4KnzOG2M74kh1Q";
var sheet = SpreadsheetApp.openById(SHEET_ID).getActiveSheet();

function doGet(e) {
  var dados = sheet.getDataRange().getValues();
  var alunos = [];

  for (var i = 1; i < dados.length; i++) {
    alunos.push({
      nome: dados[i][0],
      idade: dados[i][1],
      cidades: dados[i][2]
    })
  }
  return ContentService.createTextOutput(JSON.stringify(alunos))
                       .setMimeType(ContentService.MimeType.JSON);
}

function doPost(e) {
  var dados = JSON.parse(e.postData.contents);

  sheet.appendRow([dados.nome, dados.idade, dados.cidade]);

  var resposta = {
    status: "sucesso",
    mensagem: "Aluno cadastrado com sucesso!",
    recebido: dados
  };
  return ContentService.createTextOutput(JSON.stringify(resposta))
                       .setMimeType(ContentService.MimeType.JSON);
}
