// AULA 2: permitir "include" de HTML 

function DW57_include(filename) {
  return HtmlService.createHtmlOutputFromFile(filename).getContent();
}

function doGet() {
  // return HtmlService
  //   .createTemplateFromFile("DW57_Index")
  //   .evaluate()
  //   .setTitle("DW57");

  var template = HtmlService.createTemplateFromFile("DW57_Index");

  // Criamos uma variável que o HTML poderá ler

  // <?= ?> Tag para variável
  // <? ?> Tag para comando

  // template.nomeUsuario = "Thiago Borsatto";
  template.nomeUsuario = "Admin";
  template.dataHoje = new Date().toLocaleDateString();

  return template.evaluate().setTitle("AULA 2 - Exemplo com Variável");
}
