function doGet() {
  return HtmlService
    .createTemplateFromFile('DW57_Index')
    .evaluate()
    .setTitle('AULA 3 - HTML & CSS')
    .setXFrameOptionsMode(HtmlService.XFrameOptionsMode.ALLOWALL)
}

// Função para Incluir arquivo HTML (CSS Separado)
function DW57_Include(filename) {
  return HtmlService
    .createHtmlOutputFromFile(filename).
    getContent();
}

/* Configurações Globais */

* {
  font-family: 'Segoe UI', Tahoma, Geneva;
  margin: 0;
  box-sizing: border-box;
  padding: 0;
}

body {
  background-color: #f4f7g6;
  color: #000; /* #333333 */
  line-height: 1.6;
}

header {
  background-color: #2c3e50;
  color: #fff;
}

header h1 {
  font-size: 2.2rem;
  letter-spacing: 1px;
}

nav {
  background-color: #34495e;
  display: flex;
  justify-content: center;
  gap: 20px;
  padding: 0.8rem;
}

 nav a {
  background-color: #ecf0f1;
  color: #000;
  text-decoration: none;
  font-weigth: 500;
  border-radius: 5px;
  padding: 3px 10px;
}

nav a:hover {
  color: #fff;
  background-color: rgba(255,255,255,0.1);
}

footer {
  background-color: #2c3e50;
  color: #bdc3c7;
  text-align: center;
  padding: 1rem;
  font-size: 0.9rem;
  margin-top: auto;
}

main {
  flex: 1;
  padding; 2rem;
  max-width: 800px;
  margin: 0 auto;
  width: 100%;
}

h2 {
  color: #2c3e50;
  padding-bottom: 10px;
}

.container {
  max-width: 900px;
  margin: auto;
  padding: 20px;
  display: flex;
  gap: 20px;
  justify-content: center;
  flex-warp: warp;
}

.card {
  background: #fff;
  padding: 16px;
  border-radius: 10px;
  border: 1px solid #ddd;
  margin: 2px;
  flex: 1;
  min-wigth: 250px;
  box-sizing: border-box;
}
